import React, { Component } from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

class CartPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            products: null
        }
    }

    componentDidMount() {
        this.getProducts();
    }

    getProducts = () => {
        if (localStorage.length) {

            let localCart = localStorage.getItem('cart')
            localCart = JSON.parse(localCart)
            let ids = []
            for (let i = 0; i < localCart.length; i++) {
                ids.push(localCart[i].id)
            }
            let url = `?id=${ids.join('-')}`
            fetch(url)
                .then(res => res.json())
                .then(products => this.setState({ products: products }))
        }
    }

    render() {
        console.log(this.state);

        function createData(name, price) {
            return { name, price };
        }

        const products = [
            createData('testProduct', 200),
            createData('test2', 574)
        ]

        return (
            <div className="row" id="cart">
                {
                    localStorage.length ? (
                        <div>
                            <TableContainer>
                                <Table className="cartTable">
                                    <TableHead>
                                        <TableRow>
                                            <TableCell className="tableHead">Name </TableCell>
                                            <TableCell align="right" className="tableHead">Price </TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {products.map(product => (
                                            <TableRow>
                                                <TableCell component="th" scope="row">{product.name}</TableCell>
                                                <TableCell align="right">{product.price}</TableCell>
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        </div>
                    )
                        : (<div id="emptyCart"><div id="emptyCartIcon" class="material-icons">
                            remove_shopping_cart
                            </div>
                            <h3>Cart is empty</h3>
                        </div>
                        )}
            </div>
        )
    }
}

export default CartPage
