import React, { Component } from 'react';
import ProductCards from '../components/ProductCards';
import { Route, Switch } from 'react-router-dom';
import FilterBar from '../components/FilterBar'

class CategoryPage extends Component {
    state = { products: {} }
    updateProductsFunc = (newProducts) => {
        this.setState({ products: newProducts })
    }

    render() {
        console.log(this.state)
        return (
            <div>
                <Route path='/c/:category' render={(props) => <FilterBar {...props} products={this.state.products} />} />
                <Route path='/c/:category' render={(props) => <ProductCards {...props} updateProducts={this.updateProductsFunc} />} />
            </div>
        )
    }
}

export default CategoryPage
